            page:state.page-1
        })

    }
    return (
        
      <div className='container my-3'>
        <h2 className='text-center'>News-Monkey Top-Headlines</h2>
        <div className='container justify-content-center'>{state.loading && <Spinner/>}</div>
        <InfiniteScroll
            dataLength={state.articles.length}
            next={fetchmoredata}
            hasMore={state.articles.length!==state.totalResults.length}
            loader={<><Spinner/></>}
        >
        <div className='row'>
            { state.articles.map((element)=>{
                return <div className='col-md-4' key={element.url}>
                    <Newsitem title={element.title} imageurl={element.urlToImage} description={element.description} newsurl={element.url}/>
                </div>
            })}
            </div>
            <div className='container d-flex justify-content-between'>
            <button disabled={page<=1} type="button" className="btn btn-secondary" onClick={handleonprev}>&larr; Previous</button>
            <button type="button" disabled={page+1 > Math.ceil(totalResults/20)} className="btn btn-primary" onClick={handleonnext}>Next &rarr;</button>
            </div>
            </InfiniteScroll>
        </div>
    )
  }
