        this.setState({
            articles:this.state.articles.concateparseddata.articles
        })

        this.setState({
            page:this.state.page-1
        })
        this.setState({loading:false})
    }
    render(){
    return (
        
      <div className='container my-3'>
        <h2 className='text-center'>News-Monkey Top-Headlines</h2>
        {/* <div className='container justify-content-center'>{this.state.loading && <Spinner/>}</div> */}
        <InfiniteScroll>
            datalenght={this.state.articles.length}
            next={this.fetchmoredata}
            hasMore={this.state.articles.length!==this.state.totalResults.length}
            loader={<><Spinner/></>}
        
        <div className='row'>
            {!this.state.loading && this.state.articles.map((element)=>{
                return <div className='col-md-4' key={element.url}>
                    <Newsitem title={element.title} imageurl={element.urlToImage} description={element.description} newsurl={element.url}/>
                </div>
            })}
            </div>
            <div className='container d-flex justify-content-between'>
            <button disabled={this.state.page<=1} type="button" className="btn btn-secondary" onClick={this.handleonprev}>&larr; Previous</button>
            <button type="button" disabled={this.state.page+1 > Math.ceil(this.state.totalResults/20)} className="btn btn-primary" onClick={this.handleonnext}>Next &rarr;</button>
            </div>
            </InfiniteScroll>
        </div>
    )
  }
}

export default News
