import React, { Component } from 'react'
import { BrowserRouter, Route, Routes } from 'react-router-dom'
import Navbar from './components/Navbar'
import News from "./components/News"
import LoadingBar from 'react-top-loading-bar'
export default class App extends Component {
  state={
    progress:0
  }
  setProgress=(progress)=>{
    this.setState({progress:progress})
  }
  render() {
    return (
      <div>
        <BrowserRouter>
       <Navbar/>
       <LoadingBar
        color='#f11946'
        progress={this.state.progress}
        onLoaderFinished={() => setProgress(0)}
      />
      <Routes>
       <Route Exact path="/" element={<><News setProgress={this.setProgress} key="g" pagesize={5} country="in" category="general"/></>}></Route>
       <Route Exact path="/business" element={<><News setProgress={this.setProgress} key="b" pagesize={20} country="in" category="business"/></>}></Route>
       <Route Exact path="/entertainment" element={<><News setProgress={this.setProgress} key="e" pagesize={20} country="in" category="entertainment"/></>}></Route>
       <Route Exact path="/science" element={<><News setProgress={this.setProgress} key="s" pagesize={20} country="in" category="science"/></>}></Route>
       <Route Exact path="/sports" element={<><News setProgress={this.setProgress} pagesize={20} country="in" category="sports"/></>}></Route>
       <Route Exact path="/technology" element={<><News setProgress={this.setProgress} key="t" pagesize={20} country="in" category="technology"/></>}></Route>
       <Route Exact path="/health" element={<><News setProgress={this.setProgress} key="h" pagesize={20} country="in" category="health"/></>}></Route>
       </Routes>
       </BrowserRouter>
      </div>
    )
  }
}
